--[[	DO COMPLEXITY ANALYSIS]]--function LinearSearch(x, A)	n = #A	for j = 1, n do		if x == A[j] then			return j		end	end	return 0end function main() -- IGNORE	n = 0	A = {}	x = nil		print("Enter the length of the array: \t")	n = io.read()		print("Enter the elements of the array (each on a line): \n")	for i = 1, n do		A[i] = io.read()	end		print("Enter search element X: \t")	x = io.read()		index = LinearSearch(x, A)	print("The element is at index: \t"..index)endmain()